t.script:
// Add JavaScript for dynamic slot loading
document.addEventListener('DOMContentLoaded', function() {
    const serviceSelect = document.getElementById('service_id');
    const doctorSelect = document.getElementById('doctor_id');
    const dateInput = document.getElementById('appointment_date');
    const slotSelect = document.getElementById('slot_id');

    // Load doctors when service is selected
    function loadDoctors() {
        const serviceId = serviceSelect.value;

        if (serviceId) {
            doctorSelect.innerHTML = 'Loading...';
            slotSelect.innerHTML = 'First select doctor and date';

            // Make AJAX request to get doctors for this service
            fetch('/clinic/booking/doctors', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'X-Requested-With': 'XMLHttpRequest'
                },
                body: JSON.stringify({
                    jsonrpc: '2.0',
                    method: 'call',
                    params: {
                        service_id: serviceId
                    }
                })
            })
            .then(response => response.json())
            .then(data => {
                doctorSelect.innerHTML = 'Select Doctor';
                if (data.result && data.result.length > 0) {
                    data.result.forEach(doctor => {
                        const option = document.createElement('option');
                        option.value = doctor.id;
                        option.textContent = 'Dr. ' + doctor.name + ' - ' + doctor.specializations;
                        doctorSelect.appendChild(option);
                    });
                } else {
                    doctorSelect.innerHTML = 'No doctors available for this service';
                }
            })
            .catch(error => {
                console.error('Error loading doctors:', error);
                doctorSelect.innerHTML = 'Error loading doctors';
            });
        } else {
            doctorSelect.innerHTML = 'First select a service';
            slotSelect.innerHTML = 'First select doctor and date';
        }
    }

    function loadSlots() {
        const doctorId = doctorSelect.value;
        const date = dateInput.value;

        if (doctorId && date) {
            // Clear slots
            slotSelect.innerHTML = 'Loading...';

            // Make AJAX request to get slots
            fetch('/clinic/booking/slots', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'X-Requested-With': 'XMLHttpRequest'
                },
                body: JSON.stringify({
                    jsonrpc: '2.0',
                    method: 'call',
                    params: {
                        doctor_id: doctorId,
                        date_str: date
                    }
                })
            })
            .then(response => response.json())
            .then(data => {
                slotSelect.innerHTML = 'Select Time Slot';
                if (data.result && data.result.length > 0) {
                    data.result.forEach(slot => {
                        const option = document.createElement('option');
                        option.value = slot.id;
                        option.textContent = slot.start_time + ' - ' + slot.end_time;
                        slotSelect.appendChild(option);
                    });
                } else {
                    slotSelect.innerHTML = 'No slots available';
                }
            })
            .catch(error => {
                console.error('Error loading slots:', error);
                slotSelect.innerHTML = 'Error loading slots';
            });
        } else {
            slotSelect.innerHTML = 'First select doctor and date';
        }
    }

    serviceSelect.addEventListener('change', loadDoctors);
    doctorSelect.addEventListener('change', loadSlots);
    dateInput.addEventListener('change', loadSlots);

    // Set minimum date to today
    const today = new Date().toISOString().split('T')[0];
    dateInput.setAttribute('min', today);
});
